  methods: {
    handleJump(item, noJump) {
      if (noJump) return;
      const {
        path,
        name,
        text
      } = item;
      if (!path) return;
      if (path == "phone") {
        if (!this.itemData?.phone) return;
      }
      const types = ["popover", "phone"];
      if (types.includes(path)) {
        this.type = path;
        this.dialogShow = true;
        return;
      }
      if (path.indexOf("http") != -1) {
        if (window.plus) {
          plus.runtime.openURL(path);
          return;
        } else {
          window.open(path);
        }
      } else if (path.indexOf("/#/") != -1) {
        let query = {
          title: name || text
        };
        if (window.plus) {
          query.url = `${this.itemData.app_url}${path}?lang=${getStorage("lang") || "en"}`;
        } else {
          query.url = `${window.origin}${path}?lang=${getStorage("lang") || "en"}`;
        }
        this.$router.push({
          path: "/iframe",
          query
        });
      } else {
        if (path == "/register") {
          if (this.isLogin()) {
            _Dialog.confirm({
              title: this.$t("您已登录"),
              message: this.$t("是否跳转到注册页面"),
              confirmButtonText: this.$t("确认"),
              cancelButtonText: this.$t("取消")
            }).then(() => {
              this.removeStorage("token");
              this.$router.push("/register");
            });
          } else {
            this.$router.push("/register");
          }
          return;
        }
        const arr = ["/order", "/collectGoods"];
        if (arr.includes(path)) {
          if (!this.isLogin()) {
            this.$notifyWarn(this.$t("请先登录"));
            this.$router.push("/login");
          } else {
            this.$router.push(path);
          }
          return;
        }
        if (this.$route.path != path) {
          this.$router.push(path);
        } else {
          scrollTo(0, 0);
        }
      }
    },
    async subscription() {
