<script lang="jsx">
import { defineComponent, ref, onMounted, watch } from 'vue'
import { Form } from 'ant-design-vue'
import { ApiPrize } from '@/api/api.js'

export default defineComponent({
  props: { value: { type: Array, required: true } },
  emits: ['update:value'],
  setup(props, { emit }) {
    const fallbackImg =
      'data:image/png;base64,'
    const prizeType = [
      { label: '实物', value: 1 },
      { label: '彩金', value: 2 },
      { label: '谢谢惠顾', value: 3 }
    ]

    const formItemContext = Form.useInjectFormItemContext()
    const triggerChange = changeValue => {
      emit('update:value', changeValue)
      formItemContext.onFieldChange()
    }

    const loading = ref(false)
    const dataSource = ref([])
    const columns = ref([
      { title: '奖品ID', dataIndex: 'uuid' },
      {
        title: '图片',
        dataIndex: 'image',
        customRender: ({ text, record }) => {
          return <a-image src={record.image || ''} fallback={fallbackImg} width={50} height={50} />
        }
      },
      { title: '奖品名称', dataIndex: 'prizeName' },
      {
        title: '奖品类型',
        dataIndex: 'prizeType',
        customRender: ({ text, record }) => {
          return prizeType.find(item => item.value === record.prizeType)?.label || '谢谢惠顾'
        }
      },
      { title: '奖品价值', dataIndex: 'defaultPrize' },
      {
        title: '中奖率(≤1)',
        dataIndex: 'odds',
        fixed: 'right',
        customRender: ({ text, record }) => {
          return (
            <a-input-number
              value={record.odds}
              max={1}
              min={0}
              step={0.1}
              onChange={value => {
                record.odds = value
              }}
            />
          )
        }
      }
    ])

    const getLists = async () => {
      if (loading.value) {
        return
      }
      loading.value = true
      try {
        const dataRes = await ApiPrize({ pageNum: 0, pageSize: 999 })
        const lists = dataRes.data.records ?? []
        dataSource.value = lists.map(item => ({
          uuid: item.uuid,
          image: item.image,
          maxQuantity: item.maxQuantity,
          odds: item.odds,
          poolId: item.uuid,
          prizeAmount: item.prizeAmount,
          prizeName: item.prizeNameCn,
          prizeType: item.prizeType,
          defaultPrize: item.defaultPrize
        }))
      } catch (error) {
        console.log(error)
      } finally {
        loading.value = false
      }
    }
    onMounted(() => {
      getLists()
    })
    watch(
      dataSource,
      nval => {
        triggerChange(nval)
      },
      { deep: true }
    )
    return {
      dataSource,
      columns
    }
  }
})
</script>
<template>
  <a-card :bodyStyle="{ padding: 0 }">
    <a-table size="small" :columns="columns" :dataSource="dataSource" :scroll="{ x: 'max-content' }" :pagination="false"></a-table>
  </a-card>
</template>
